union all select #{rowNum}
) numbers on char_length(kw.route) - char_length(replace(kw.route, '.', ''))>=numbers.n-1 ) descendant_ancestor_mapping join kb_permission_range kpr on ( kpr.organization_id = descendant_ancestor_mapping.organization_id and (kpr.project_id = 0 and descendant_ancestor_mapping.project_id is null or kpr.project_id = descendant_ancestor_mapping.project_id) and kpr.target_type in ('FILE_ORG','FOLDER_ORG','FILE_PROJECT','FOLDER_PROJECT') and kpr.target_value = descendant_ancestor_mapping.ancestor_id
)
group by descendant_ancestor_mapping.descendant_id ) or /*知识库权限*/ kw.base_id in (select kpr.target_value from kb_permission_range kpr where (kpr.organization_id = kw.organization_id and (kpr.project_id = 0 and kw.project_id is null or kpr.project_id = kw.project_id) and kpr.target_type in ('KNOWLEDGE_BASE_ORG', 'KNOWLEDGE_BASE_PROJECT') and kpr.target_value = kw.base_id
)))

