mapper.select:
SELECT
CASE COUNT(1)
WHEN 0 THEN FALSE ELSE TRUE END
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null


AND kw.is_delete = 0

SELECT
MAX(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
MIN(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
kw.RANK
FROM
kb_workspace kw
WHERE kw.ID = #{id}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
MAX(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}
AND kw.rank < #{rightRank}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT
MIN(kw.rank)
FROM
kb_workspace kw
WHERE kw.parent_id = #{parentId}
AND kw.rank > #{leftRank}

    AND kw.organization_id = #{organizationId}



        AND kw.project_id = #{projectId}


        AND kw.project_id is null

SELECT kw.* FROM kb_workspace kw WHERE
kw.parent_id IN
#{parentId}
AND kw.organization_id = #{organizationId}
AND kw.project_id = #{projectId}
AND kw.project_id is null
kw.parent_id = -1

mapper.update:
UPDATE kb_workspace
SET ROUTE = REPLACE(ROUTE,#{oldRoute},#{newRoute})
WHERE ROUTE REGEXP '^${oldRoute}.'

    AND organization_id = #{organizationId}



        AND project_id = #{projectId}


        AND project_id is null

UPDATE kb_workspace
SET is_delete = #{delete}
WHERE ROUTE REGEXP '^${route}.'

    AND organization_id = #{organizationId}



        AND project_id = #{projectId}


        AND project_id is null

mapper.select:
SELECT * FROM kb_workspace kw WHERE 1 = 1
AND kw.base_id = #{baseId}
AND kw.organization_id = #{organizationId}
AND kw.type ='folder'
AND (kw.type ='folder' or kw.type ='document')
AND (kw.type ='folder' or kw.type ='folder')
and kw.type NOT IN
#{excludeType}
AND kw.project_id = #{projectId}
AND kw.project_id is null
AND kw.is_delete = 0 ORDER BY kw.RANK ASC

SELECT * FROM kb_workspace kw WHERE 1 = 1
AND kw.organization_id = #{organizationId}
AND kw.project_id = #{projectId}
AND kw.project_id is null
AND kw.is_delete = 1 ORDER BY kw.last_update_date DESC, kw.rank ASC

select kw.*, kkb.NAME AS base_name from kb_workspace kw JOIN kb_knowledge_base kkb ON kkb.ID = kw.base_id where 1=1
and kw.project_id = #{projectId}
and kw.id in
#{spaceId}

select MAX(LENGTH(kw.ROUTE) - LENGTH(REPLACE(kw.ROUTE,'.','')) + 1)
from kb_workspace kw


        AND kw.organization_id = #{organizationId}



            AND (kw.project_id = #{projectId} OR kw.project_id is null)


            AND kw.project_id is null



        AND kw.base_id = #{baseId}

    AND kw.is_delete = #{deleteFlag}

select id from kb_workspace
where
organization_id = #{organizationId}
and
project_id = #{projectId}
and
base_id = #{baseId}
and
parent_id = 0

SELECT * FROM ( SELECT c.base_id AS base_id, any_value (id) AS id, any_value (NAME) AS title, any_value (ROUTE) AS ROUTE, c.LAST_UPDATED_BY AS LAST_UPDATED_BY, any_value (LAST_UPDATE_DATE) AS LAST_UPDATE_DATE FROM ( SELECT wk.base_id, wk.id, wk. NAME, wk.ROUTE, kp.LAST_UPDATED_BY, kp.LAST_UPDATE_DATE FROM kb_workspace wk, kb_workspace_page wkp, kb_page kp WHERE wkp.WORKSPACE_ID = wk.ID AND wkp.PAGE_ID = kp.ID AND wk.is_delete=0 AND wk.base_id IN
#{baseId}
ORDER BY wk.base_id, kp.LAST_UPDATED_BY, kp.LAST_UPDATE_DATE DESC LIMIT 9999 ) c GROUP BY c.base_id, c.LAST_UPDATED_BY ) t WHERE 3 > ( SELECT count(1) FROM ( SELECT c.base_id AS base_id, any_value (id) AS id, any_value (NAME) AS title, any_value (ROUTE) AS ROUTE, c.LAST_UPDATED_BY AS LAST_UPDATED_BY, any_value (LAST_UPDATE_DATE) AS LAST_UPDATE_DATE FROM ( SELECT wk.base_id, wk.id, wk. NAME, wk.ROUTE, kp.LAST_UPDATED_BY, kp.LAST_UPDATE_DATE FROM kb_workspace wk, kb_workspace_page wkp, kb_page kp WHERE wkp.WORKSPACE_ID = wk.ID AND wkp.PAGE_ID = kp.ID AND wk.is_delete=0 AND wk.base_id IN
#{baseId}
ORDER BY wk.base_id, kp.LAST_UPDATED_BY, kp.LAST_UPDATE_DATE DESC LIMIT 9999 ) c GROUP BY c.base_id, c.LAST_UPDATED_BY ) b WHERE b.base_id = t.base_id AND b.LAST_UPDATE_DATE > t.LAST_UPDATE_DATE ) ORDER BY LAST_UPDATE_DATE DESC

